
#include <chrono>
#include <iostream>
#include <vector>

using std::cout;
using std::endl;
using std::vector;

class Solution {
 public:
  int shortestBridge(vector<vector<int>>& grid) {
    const int n = grid.size();
    // find one land on any island.
    bool keep_running = true;
    int row = -1, column = -1;
    for (int i = 0; (i < n) && keep_running; ++i)
      for (int j = 0; (j < n) && keep_running; ++j)
        if (grid[i][j] == 1) {
          row = i;
          column = j;
          keep_running = false;
        }

    // first layer.
    vector<int> one_island, temp_island, next_island;
    temp_island.push_back(row * n + column);
    grid[row][column] = -1;
    do {
      for (const int pos : temp_island) {
        one_island.push_back(pos);

        // up
        if (pos / n > 0 && grid[pos / n - 1][pos % n] == 1) {
          next_island.push_back(pos - n);
          grid[next_island.back() / n][next_island.back() % n] = -1;
        }

        // down
        if (pos / n + 1 < n && grid[pos / n + 1][pos % n] == 1) {
          next_island.push_back(pos + n);
          grid[next_island.back() / n][next_island.back() % n] = -1;
        }

        // left
        if (pos % n > 0 && grid[pos / n][pos % n - 1] == 1) {
          next_island.push_back(pos - 1);
          grid[next_island.back() / n][next_island.back() % n] = -1;
        }

        // right
        if (pos % n + 1 < n && grid[pos / n][pos % n + 1] == 1) {
          next_island.push_back(pos + 1);
          grid[next_island.back() / n][next_island.back() % n] = -1;
        }
      }
      temp_island.swap(next_island);
      next_island.clear();
    } while (!temp_island.empty());

    int answer = 0;
    temp_island = one_island;
    next_island.clear();
    do {
      for (const int pos : temp_island) {
        // up
        if (pos / n > 0 && grid[pos / n - 1][pos % n] != -1) {
          next_island.push_back(pos - n);
          if (grid[next_island.back() / n][next_island.back() % n] == 1) return answer;
          grid[next_island.back() / n][next_island.back() % n] = -1;
        }

        // down
        if (pos / n + 1 < n && grid[pos / n + 1][pos % n] != -1) {
          next_island.push_back(pos + n);
          if (grid[next_island.back() / n][next_island.back() % n] == 1) return answer;
          grid[next_island.back() / n][next_island.back() % n] = -1;
        }

        // left
        if (pos % n > 0 && grid[pos / n][pos % n - 1] != -1) {
          next_island.push_back(pos - 1);
          if (grid[next_island.back() / n][next_island.back() % n] == 1) return answer;
          grid[next_island.back() / n][next_island.back() % n] = -1;
        }

        // right
        if (pos % n + 1 < n && grid[pos / n][pos % n + 1] != -1) {
          next_island.push_back(pos + 1);
          if (grid[next_island.back() / n][next_island.back() % n] == 1) return answer;
          grid[next_island.back() / n][next_island.back() % n] = -1;
        }
      }
      temp_island.swap(next_island);
      next_island.clear();
      ++answer;
    } while (!temp_island.empty());
    return answer;
  }
};

