using std::vector;

class Solution {
 public:
  int minStoneSum(vector<int>& piles, int k) {

    std::sort(piles.rbegin(), piles.rend());
    for (int i = 0; i < k; ++i) {
      piles[0] -= piles[0] / 2;
      int pos = 0;
      while (pos + 1 < piles.size() && piles[pos] < piles[pos + 1]) {
        piles[pos] ^= piles[pos + 1];
        piles[pos + 1] ^= piles[pos];
        piles[pos] ^= piles[pos + 1];
        ++pos;
      }
    }
    return std::accumulate(piles.cbegin(), piles.cend(), 0);
  }
};

